import React, { useState, useEffect } from 'react';
import {
  StyleSheet,
  View,
  Text,
  TextInput,
  TouchableOpacity,
  FlatList,
  ActivityIndicator,
  Platform,
} from 'react-native';
import axios from 'axios';
import { API_KEY, BASE_URL } from '../config';

const popularStocks = ['AAPL', 'GOOGL', 'MSFT', 'AMZN', 'TSLA'];

export default function HomeScreen({ navigation }) {
  const [searchQuery, setSearchQuery] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    console.log('HomeScreen mounted'); // Debug log
  }, []);

  const searchStock = async (symbol) => {
    try {
      console.log('Searching for stock:', symbol); // Debug log
      setLoading(true);
      const response = await axios.get(
        `${BASE_URL}?function=GLOBAL_QUOTE&symbol=${symbol}&apikey=${API_KEY}`
      );
      
      console.log('API Response:', response.data); // Debug log
      
      if (response.data['Global Quote'] && Object.keys(response.data['Global Quote']).length > 0) {
        navigation.navigate('StockDetail', { symbol });
      } else {
        alert('Stock not found');
      }
    } catch (error) {
      console.error('API Error:', error); // Debug log
      alert('Error fetching stock data: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const renderStockItem = ({ item }) => (
    <TouchableOpacity
      style={styles.stockItem}
      onPress={() => searchStock(item)}
    >
      <Text style={styles.stockSymbol}>{item}</Text>
    </TouchableOpacity>
  );

  return (
    <View style={styles.container}>
      <View style={styles.searchContainer}>
        <TextInput
          style={styles.searchInput}
          placeholder="Enter stock symbol (e.g., AAPL)"
          value={searchQuery}
          onChangeText={setSearchQuery}
          onSubmitEditing={() => searchQuery && searchStock(searchQuery)}
        />
        <TouchableOpacity
          style={styles.searchButton}
          onPress={() => searchQuery && searchStock(searchQuery)}
        >
          <Text style={styles.buttonText}>Search</Text>
        </TouchableOpacity>
      </View>

      {loading ? (
        <View style={styles.loadingContainer}>
          <ActivityIndicator size="large" color="#007AFF" />
          <Text style={styles.loadingText}>Loading...</Text>
        </View>
      ) : (
        <>
          <Text style={styles.popularText}>Popular Stocks</Text>
          <FlatList
            data={popularStocks}
            renderItem={renderStockItem}
            keyExtractor={(item) => item}
            style={styles.list}
            ListEmptyComponent={<Text style={styles.emptyText}>No stocks available</Text>}
          />
        </>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
    backgroundColor: '#fff',
  },
  searchContainer: {
    flexDirection: 'row',
    marginBottom: 20,
  },
  searchInput: {
    flex: 1,
    height: 40,
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 8,
    paddingHorizontal: 10,
    marginRight: 10,
    ...(Platform.OS === 'web' ? {
      outlineStyle: 'none',
    } : {}),
  },
  searchButton: {
    backgroundColor: '#007AFF',
    padding: 10,
    borderRadius: 8,
    justifyContent: 'center',
  },
  buttonText: {
    color: '#fff',
    fontWeight: 'bold',
  },
  popularText: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  list: {
    flex: 1,
  },
  stockItem: {
    padding: 15,
    borderBottomWidth: 1,
    borderBottomColor: '#eee',
    cursor: Platform.OS === 'web' ? 'pointer' : 'default',
  },
  stockSymbol: {
    fontSize: 16,
  },
  loadingContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  loadingText: {
    marginTop: 10,
    color: '#666',
  },
  emptyText: {
    textAlign: 'center',
    color: '#666',
    marginTop: 20,
  },
});
